import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Ajoute Axios pour les appels API
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  TextField,
  Button,
} from '@mui/material';

const ProductList = () => {
  const [products, setProducts] = useState([]); // Initialement vide, les données viennent du backend
  const [editingRow, setEditingRow] = useState(null);

  // Charger les données depuis le backend
  useEffect(() => {
    axios
      .get('http://localhost:3000/api/products') // Remplace avec l'URL de ton API backend
      .then((response) => {
        setProducts(response.data);
      })
      .catch((error) => {
        console.error('Erreur lors de la récupération des produits :', error);
      });
  }, []);

  const handleEdit = (id) => {
    setEditingRow(id);
  };

  const handleSave = (id) => {
    const updatedProduct = products.find((product) => product._id === id);

    axios
      .put(`http://localhost:5000/api/products/${id}`, updatedProduct) // Envoie la mise à jour au backend
      .then((response) => {
        console.log('Produit mis à jour :', response.data);
        setEditingRow(null); // Quitte le mode édition après la sauvegarde
      })
      .catch((error) => {
        console.error('Erreur lors de la mise à jour :', error);
      });
  };
  const [showAddForm, setShowAddForm] = useState(false);

const handleAddProduct = (newProduct) => {
  axios
    .post('http://localhost:5000/api/products', newProduct)
    .then((response) => {
      setProducts((prevProducts) => [...prevProducts, response.data]);
      setShowAddForm(false); // Masquer le formulaire après l'ajout
    })
    .catch((error) => console.error('Erreur lors de l’ajout :', error));
};

  const handleChange = (id, field, value) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product._id === id ? { ...product, [field]: value } : product
      )
    );
  };

  return (
    <TableContainer component={Paper}>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Nom</TableCell>
            <TableCell>Type</TableCell>
            <TableCell>Prix (€)</TableCell>
            <TableCell>Note</TableCell>
            <TableCell>Garantie (ans)</TableCell>
            <TableCell>Disponible</TableCell>
            <TableCell>Actions</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {products.map((product) => (
            <TableRow key={product._id}>
              <TableCell>
                {editingRow === product._id ? (
                  <TextField
                    value={product.name}
                    onChange={(e) =>
                      handleChange(product._id, 'name', e.target.value)
                    }
                  />
                ) : (
                  product.name
                )}
              </TableCell>
              <TableCell>
                {editingRow === product._id ? (
                  <TextField
                    value={product.type}
                    onChange={(e) =>
                      handleChange(product._id, 'type', e.target.value)
                    }
                  />
                ) : (
                  product.type
                )}
              </TableCell>
              <TableCell>
                {editingRow === product._id ? (
                  <TextField
                    type="number"
                    value={product.price}
                    onChange={(e) =>
                      handleChange(product._id, 'price', parseFloat(e.target.value) || 0)
                    }
                  />
                ) : (
                  product.price.toFixed(2)
                )}
              </TableCell>
              <TableCell>
                {editingRow === product._id ? (
                  <TextField
                    type="number"
                    value={product.rating}
                    onChange={(e) =>
                      handleChange(product._id, 'rating', parseFloat(e.target.value) || 0)
                    }
                  />
                ) : (
                  product.rating
                )}
              </TableCell>
              <TableCell>
                {editingRow === product._id ? (
                  <TextField
                    type="number"
                    value={product.warranty_years}
                    onChange={(e) =>
                      handleChange(product._id, 'warranty_years', parseInt(e.target.value) || 0)
                    }
                  />
                ) : (
                  product.warranty_years
                )}
              </TableCell>
              <TableCell>
                {editingRow === product._id ? (
                  <TextField
                    value={product.available ? 'Oui' : 'Non'}
                    onChange={(e) =>
                      handleChange(
                        product._id,
                        'available',
                        e.target.value.toLowerCase() === 'oui'
                      )
                    }
                  />
                ) : (
                  product.available ? 'Oui' : 'Non'
                )}
              </TableCell>
              <TableCell>
                {editingRow === product._id ? (
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={() => handleSave(product._id)}
                  >
                    Sauvegarder
                  </Button>
                ) : (
                  <Button
                    variant="outlined"
                    color="secondary"
                    onClick={() => handleEdit(product._id)}
                  >
                    Modifier
                  </Button>
                )}
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default ProductList;
